SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS atendimento_visita_atual ON atendimento_visita CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_atendimento_visita_atual() RETURNS trigger AS $BODY$
DECLARE
ie_atend_visita_lib_w varchar(1);
ie_visita_lib_w varchar(1);
BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'S')  then	

    IF (NEW.NR_SEQ_CONTROLE IS NOT NULL) AND
        ((OLD.NR_SEQ_CONTROLE IS NULL) OR (NEW.NR_SEQ_CONTROLE != OLD.NR_SEQ_CONTROLE)) THEN
        CALL VALIDA_CRACHA_INTEGRACAO(NEW.NM_USUARIO, NEW.NR_SEQ_CONTROLE);
    END IF;

    if 	((TG_OP = 'INSERT') and (NEW.nr_atendimento is not null) and (coalesce(NEW.ie_paciente,'N') = 'N')) or  
        ((TG_OP = 'UPDATE') and (NEW.nr_atendimento is not null) and (coalesce(NEW.ie_paciente,'N') = 'N') and (NEW.dt_saida is null)) then
            select	CASE WHEN count(1)=0 THEN  'N'  ELSE 'S' END 
        into STRICT	ie_atend_visita_lib_w
        from	atendimento_visita_lib
        where	nr_atendimento = NEW.nr_atendimento;

        if (coalesce(ie_atend_visita_lib_w,'N') = 'S') then 
            SELECT 	CASE WHEN COUNT(*)=0 THEN 'N'  ELSE 'S' END 
            into STRICT	ie_visita_lib_w
            FROM 	atendimento_visita_lib 
            WHERE 	nr_atendimento  = NEW.nr_atendimento 
            and 	((cd_pessoa_lib is not null and cd_pessoa_lib = NEW.cd_pessoa_fisica) or (nm_pessoa_liberacao is not null and upper(nm_pessoa_liberacao) = upper(NEW.nm_visitante)));

            if (coalesce(ie_visita_lib_w,'S') = 'N') then
                CALL Wheb_mensagem_pck.exibir_mensagem_abort(41594);			
            end if;
        end if;
    end if;
end if;
RETURN NEW;
END;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_atendimento_visita_atual() FROM PUBLIC;

CREATE TRIGGER atendimento_visita_atual
	BEFORE INSERT OR UPDATE ON atendimento_visita FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_atendimento_visita_atual();

