SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS atendimento_visita_insert ON atendimento_visita CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_atendimento_visita_insert() RETURNS trigger AS $BODY$
declare
nr_seq_controle_w	bigint;
ie_controla_w		varchar(5);
ie_considera_estab_w	varchar(5);
ie_controle_diferente_w	varchar(5);
ie_possui_w		varchar(5);
ie_possui_alta_w	varchar(2);
ie_permite_alta_w     varchar(2);
nr_controle_unico_w		varchar(2);
ds_origem_w			varchar(1800);

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'S')  then
	ie_controla_w := Obter_Param_Usuario(44, 10, obter_perfil_ativo, NEW.nm_usuario, obter_estabelecimento_ativo, ie_controla_w);
	ie_considera_estab_w := Obter_Param_Usuario(8014, 59, obter_perfil_ativo, NEW.nm_usuario, obter_estabelecimento_ativo, ie_considera_estab_w);
	ie_controle_diferente_w := Obter_Param_Usuario(8014, 97, obter_perfil_ativo, NEW.nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_controle_diferente_w);
	ie_permite_alta_w := Obter_Param_Usuario(8014, 112, obter_perfil_ativo, NEW.nm_usuario, obter_estabelecimento_ativo, ie_permite_alta_w);
	nr_controle_unico_w := Obter_Param_Usuario(8014, 119, obter_perfil_ativo, NEW.nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, nr_controle_unico_w);

	Select 	coalesce(max('S'),'N')
	into STRICT 	ie_possui_alta_w
	from 	atendimento_paciente a
	where 	NEW.nr_atendimento = a.nr_atendimento
	and 	a.dt_alta is not null;

	if (ie_possui_alta_w = 'S') and (ie_permite_alta_w = 'N')	then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(284133);
	end if;


	if (ie_controla_w = 'S') and (NEW.nr_seq_controle is null) then

			if (ie_considera_estab_w = 'S') then
				nr_seq_controle_w	:=	obter_nr_controle_estab(wheb_usuario_pck.get_cd_estabelecimento);
			
			elsif (nr_controle_unico_w = 'S') then
					select	nextval('controle_visita_unico_seq')
					into STRICT	nr_seq_controle_w
					;
			else
				select	nextval('atendimento_visita_seq2')
				into STRICT	nr_seq_controle_w
				;
			end if;

		NEW.nr_seq_controle	:= nr_seq_controle_w;
	end if;

	ds_origem_w := substr(dbms_utility.format_call_stack,1,1800);
		
	insert into log_mov(
		dt_atualizacao,
		nm_usuario,
		cd_log,
		ds_log)
	values (	LOCALTIMESTAMP,
		'Tasy',
		131713,
		'obter_perfil_ativo: '||obter_perfil_ativo||
		' - :new.nm_usuario: '||NEW.nm_usuario||
		' - :new.nr_seq_controle: '||NEW.nr_seq_controle||
		' - Stack: ' || ds_origem_w);

	select	nextval('controle_visita_seq')
	into STRICT	NEW.nr_controle_acesso
	;

	if (ie_controle_diferente_w = 'S') then

		nr_seq_controle_w	:= obter_novo_numero_controle(NEW.nr_seq_controle);
		NEW.nr_seq_controle	:= nr_seq_controle_w;

	end if;
end if;	
RETURN NEW;
END;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_atendimento_visita_insert() FROM PUBLIC;

CREATE TRIGGER atendimento_visita_insert
	BEFORE INSERT ON atendimento_visita FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_atendimento_visita_insert();

