SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS atend_aval_srpa_atual ON atend_aval_srpa CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_atend_aval_srpa_atual() RETURNS trigger AS $BODY$
declare

qt_reg_w		smallint;
ie_tipo_w		varchar(10);
ie_libera_partic_w	varchar(10);
nr_atendimento_w   bigint;
cd_pessoa_fisica_w varchar(10);

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

if (NEW.nr_seq_pepo > 0)  then
	select	max(c.nr_atendimento),
		max(c.cd_pessoa_fisica)
	into STRICT	nr_atendimento_w,
		cd_pessoa_fisica_w
	from	pepo_cirurgia c
	where	c.nr_sequencia = NEW.nr_seq_pepo;
else
	select	max(c.nr_atendimento),
			max(c.cd_pessoa_fisica)
	into STRICT	nr_atendimento_w,
			cd_pessoa_fisica_w
	from	cirurgia c
	where	c.nr_cirurgia = NEW.nr_cirurgia;

end if;

	if (NEW.dt_liberacao is null) then
		ie_tipo_w := 'LAVAL';
	elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
		ie_tipo_w := 'XLAVAL';
	end if;

	if (ie_tipo_w	is not null) then
		CALL Gerar_registro_pendente_PEP(ie_tipo_w, NEW.nr_sequencia, cd_pessoa_fisica_w, nr_atendimento_w, NEW.nm_usuario);
	end if;

<<Final>>
qt_reg_w	:= 0;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_atend_aval_srpa_atual() FROM PUBLIC;

CREATE TRIGGER atend_aval_srpa_atual
	AFTER INSERT OR UPDATE ON atend_aval_srpa FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_atend_aval_srpa_atual();

