SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS atend_categ_convenio_befinsert ON atend_categoria_convenio CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_atend_categ_convenio_befinsert() RETURNS trigger AS $BODY$
DECLARE

ie_tipo_convenio_w		smallint := 2;
ie_plano_w				smallint;
ie_regulacao_gerint_w	parametro_atendimento.ie_regulacao_gerint%type;
nr_cerih_w				gerint_solic_internacao.nr_protocolo_solicitacao%type;

BEGIN

if ('00/00/0000 00:00:00' = to_char(NEW.dt_validade_carteira,'dd/mm/yyyy hh24:mi:ss')) then
	NEW.dt_validade_carteira := null;
end if;

select coalesce(max(1), 0)
into STRICT ie_plano_w
from convenio_plano
where cd_plano = NEW.cd_plano_convenio;

if (ie_plano_w = 0) then
	NEW.cd_plano_convenio := null;
end if;

select	coalesce(max(ie_regulacao_gerint),'N')
into STRICT	ie_regulacao_gerint_w
from	parametro_atendimento
where	cd_estabelecimento	= (	SELECT	max(cd_estabelecimento)
								from	atendimento_paciente
								where 	nr_atendimento =  NEW.nr_atendimento);

if (ie_regulacao_gerint_w = 'S') then
	select	max(nr_protocolo_solicitacao)
	into STRICT	nr_cerih_w
	from	gerint_solic_internacao
	where	nr_atendimento = NEW.nr_atendimento;

	if (nr_cerih_w is not null) and (NEW.cd_senha is null) then
		NEW.cd_senha := nr_cerih_w;
	end if;
end if;

RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_atend_categ_convenio_befinsert() FROM PUBLIC;

CREATE TRIGGER atend_categ_convenio_befinsert
	BEFORE INSERT ON atend_categoria_convenio FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_atend_categ_convenio_befinsert();

