SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS atend_check_list_atual ON atend_check_list CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_atend_check_list_atual() RETURNS trigger AS $BODY$
BEGIN
 
if (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then 
	 
	CALL Gerar_comunic_aval(NEW.nr_sequencia, NEW.cd_pessoa_fisica, NEW.nr_atendimento, NEW.nr_seq_tipo_avaliacao, NEW.dt_cheklist, NEW.nm_usuario);
 
end if;
 
if (coalesce(OLD.DT_CHEKLIST,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_CHEKLIST) and (NEW.DT_CHEKLIST is not null) then 
	NEW.ds_utc		:= obter_data_utc(NEW.DT_CHEKLIST, 'HV');	
end if;
 
if (coalesce(OLD.DT_LIBERACAO,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_LIBERACAO) and (NEW.DT_LIBERACAO is not null) then 
	NEW.ds_utc_atualizacao	:= obter_data_utc(NEW.DT_LIBERACAO,'HV');
end if;
 
 
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_atend_check_list_atual() FROM PUBLIC;

CREATE TRIGGER atend_check_list_atual
	BEFORE INSERT OR UPDATE ON atend_check_list FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_atend_check_list_atual();

