SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS atend_escala_dms_atual ON atend_escala_dms CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_atend_escala_dms_atual() RETURNS trigger AS $BODY$
declare
qt_reg_w	smallint;
cd_setor_atendimento_w	bigint;
BEGIN
  BEGIN

if (NEW.nr_hora is null) or (NEW.DT_AVALIACAO <> OLD.DT_AVALIACAO) then
	BEGIN  
	NEW.nr_hora	:= (to_char(round(NEW.DT_AVALIACAO,'hh24'),'hh24'))::numeric;
	end;
end if;
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

if (NEW.nr_atendimento is not null) and (NEW.dt_liberacao is null) then
	BEGIN
	cd_setor_atendimento_w := obter_setor_atendimento(NEW.nr_atendimento);
	exception
	when others then
		cd_setor_atendimento_w	:= 0;
	end;
	
	if (cd_setor_atendimento_w	> 0) then
		NEW.cd_setor_atendimento	:= cd_setor_atendimento_w;
	end if;
end if;

NEW.qt_ponto	:= 	NEW.IE_MUDANCA_PESO +
			NEW.IE_INGESTAO_DIETETICA +
			NEW.IE_SINTOMA_GASTRO +
			NEW.IE_CAPACIDADE_FUNCIONAL +
			NEW.IE_COMORBIDADE +
			NEW.IE_RESERVA_GORDURA +
			NEW.IE_DESGASTE_MUSCULAR;
<<Final>>
qt_reg_w	:= 0;
  END;
RETURN NEW;
END;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_atend_escala_dms_atual() FROM PUBLIC;

CREATE TRIGGER atend_escala_dms_atual
	BEFORE INSERT OR UPDATE ON atend_escala_dms FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_atend_escala_dms_atual();

