SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS atend_pac_unid_classif_esp_aft ON atend_pac_unid_classif_esp CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_atend_pac_unid_classif_esp_aft() RETURNS trigger AS $BODY$
declare

reg_integracao_p		gerar_int_padrao.reg_integracao;
qt_reg_w			bigint;

pragma autonomous_transaction;
BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger = 'S')  then
	BEGIN
	select	count(1)
	into STRICT	qt_reg_w
	from	atend_paciente_unidade
	where	nr_atendimento = NEW.nr_atendimento
	and	nr_seq_interno < NEW.nr_seq_atend_pac_unidade;
	
	if (qt_reg_w > 0) then
		BEGIN
		select	count(1)
		into STRICT	qt_reg_w
		from	atend_pac_unid_classif_esp
		where	nr_atendimento = NEW.nr_atendimento
		and	nr_seq_atend_pac_unidade = NEW.nr_seq_atend_pac_unidade
		and	dt_inicial < NEW.dt_inicial;
		
		if (qt_reg_w > 0) then		
			reg_integracao_p.nr_atendimento 	:= NEW.nr_atendimento;
			reg_integracao_p.ie_saida_unidade	:= 'N';
			reg_integracao_p.ie_tipo_movimentacao	:= 'T';
			reg_integracao_p.ie_operacao		:= 'A';

			reg_integracao_p := gerar_int_padrao.gravar_integracao('118', NEW.nr_seq_atend_pac_unidade, NEW.nm_usuario, reg_integracao_p);			
		end if;
		end;
	end if;
	end;
end if;
commit;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_atend_pac_unid_classif_esp_aft() FROM PUBLIC;

CREATE TRIGGER atend_pac_unid_classif_esp_aft
	AFTER INSERT ON atend_pac_unid_classif_esp FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_atend_pac_unid_classif_esp_aft();

