CREATE OR REPLACE FUNCTION trigger_fct_atend_perda_ganho_pend_atual() RETURNS trigger AS $BODY$
declare

qt_reg_w			smallint;
ie_tipo_w			varchar(10);
ie_liberar_ganho_perda_w	varchar(10);

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  OR
	((obter_funcao_ativa <> 281) and (obter_funcao_ativa <> 1136) and (obter_funcao_ativa <> 872)	and (obter_funcao_ativa <> 10026)) then
	goto Final;
end if;

select	max(ie_liberar_ganho_perda)
into STRICT	ie_liberar_ganho_perda_w
from	parametro_medico
where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

if (coalesce(ie_liberar_ganho_perda_w,'N') = 'S') then
	if (NEW.dt_liberacao is null) then
		ie_tipo_w := 'GP';
	elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then


		ie_tipo_w := 'XGP';
	end if;
	if (ie_tipo_w	is not null) then
		CALL Gerar_registro_pendente_PEP(ie_tipo_w, NEW.nr_sequencia, obter_pessoa_atendimento(NEW.nr_atendimento,'C'), NEW.nr_atendimento, NEW.nm_usuario);
	end if;
end if;

<<Final>>
qt_reg_w	:= 0;
RETURN NEW;
end
