CREATE TRIGGER atend_sumario_alta_pend_atual
	AFTER INSERT OR UPDATE OR DELETE ON atend_sumario_alta FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_atend_sumario_alta_pend_atual();

