CREATE OR REPLACE FUNCTION trigger_fct_atestado_paciente_insert() RETURNS trigger AS $BODY$
declare
 
BEGIN 
 
if (NEW.nr_atendimento	is not null) and (NEW.cd_pessoa_fisica is null) then 
	 
	NEW.cd_pessoa_fisica	:= obter_pessoa_atendimento(NEW.nr_atendimento,'C');
	 
end if;
 
if (NEW.DT_AUSENCIA_TRABALHO is null) and (NEW.QT_DIAS_ATESTADO is not null) then 
	NEW.DT_AUSENCIA_TRABALHO := coalesce(NEW.DT_ATESTADO, LOCALTIMESTAMP) + NEW.QT_DIAS_ATESTADO;
end if;
 
RETURN NEW;
end
$BODY$
