SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS atnd_pacte_dpc_ins_upd_after ON atendimento_paciente CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_atnd_pacte_dpc_ins_upd_after() RETURNS trigger AS $BODY$
declare
	nm_usuario_w varchar(15);
	a_nm_usuario_w varchar(15);
    	DPC_CNT_W bigint;
	cd_pessoa_fisica_w ATENDIMENTO_PACIENTE.cd_pessoa_fisica%type;
	nr_atendimento_w ATENDIMENTO_PACIENTE.nr_atendimento%type;
	ie_tipo_atendimento_w ATENDIMENTO_PACIENTE.ie_tipo_atendimento%type;

c01 CURSOR FOR
SELECT distinct obter_usuario_pf(a.cd_prescritor)
from pessoa_fisica c,
     medico b,
     prescr_medica a
where b.cd_pessoa_fisica = c.cd_pessoa_fisica
and a.cd_prescritor  = b.cd_pessoa_fisica
and a.nr_atendimento = NEW.nr_atendimento
and b.ie_situacao = 'A'

union

select distinct obter_usuario_pf(a.cd_medico)
from pessoa_fisica c,
     medico b,
     evolucao_paciente a
where b.cd_pessoa_fisica = c.cd_pessoa_fisica
and a.cd_medico = b.cd_pessoa_fisica
and a.nr_atendimento  = NEW.nr_atendimento
and b.ie_situacao = 'A'

union

select distinct obter_usuario_pf(b.cd_pessoa_fisica)
from pessoa_fisica c,
     medico b
where b.cd_pessoa_fisica = c.cd_pessoa_fisica
and b.cd_pessoa_fisica  = NEW.cd_pessoa_fisica
and b.ie_situacao = 'A'

union

select distinct obter_usuario_pf(b.cd_medico)
from pessoa_fisica c,
     pf_medico_externo b,
     pessoa_fisica m
where b.cd_pessoa_fisica = c.cd_pessoa_fisica
and c.cd_pessoa_fisica = b.cd_pessoa_fisica
and m.cd_pessoa_fisica = b.cd_medico
and b.cd_pessoa_fisica = NEW.cd_pessoa_fisica;
BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger	= 'S') then

if (GET_USES_DPC() = 'S') then

	nm_usuario_w := obter_usuario_pf(NEW.cd_medico_resp);
	cd_pessoa_fisica_w:= NEW.cd_pessoa_fisica;
	nr_atendimento_w := NEW.nr_atendimento;
	ie_tipo_atendimento_w := NEW.ie_tipo_atendimento;
	
	select coalesce(count(1),0)
    into STRICT dpc_cnt_w
    from patient_dpc
    where nr_atendimento = nr_atendimento_w;
	
	if (dpc_cnt_w = 0) then
	
		if (IE_TIPO_ATENDIMENTO_W = 1) then
		insert into PEP_ITEM_PENDENTE(
				NR_SEQUENCIA,
				DT_ATUALIZACAO,
				NM_USUARIO,
				DT_ATUALIZACAO_NREC,
				NM_USUARIO_NREC,
				DT_REGISTRO,
				CD_PESSOA_FISICA,
				NR_ATENDIMENTO,
				NR_SEQ_REGISTRO,
				IE_TIPO_PENDENCIA,
				IE_TIPO_REGISTRO,
				DS_ITEM)
		SELECT nextval('pep_item_pendente_seq'),
				LOCALTIMESTAMP,
				coalesce(nm_usuario_w, wheb_usuario_pck.get_nm_usuario),
				LOCALTIMESTAMP,
				coalesce(nm_usuario_w, wheb_usuario_pck.get_nm_usuario),
				LOCALTIMESTAMP,
				cd_pessoa_fisica_w ,
				nr_atendimento_w,
				nr_atendimento_w,
				'L',
				'DPC',
				obter_desc_expressao(1046875)  as DS_ITEM;

		OPEN C01;
			LOOP
				FETCH C01 into a_nm_usuario_w;
				EXIT WHEN NOT FOUND; /* apply on c01 */
				insert into PEP_ITEM_PENDENTE(
					NR_SEQUENCIA,
					DT_ATUALIZACAO,
					NM_USUARIO,
					DT_ATUALIZACAO_NREC,
					NM_USUARIO_NREC,
					DT_REGISTRO,
					CD_PESSOA_FISICA,
					NR_ATENDIMENTO,
					NR_SEQ_REGISTRO,
					IE_TIPO_PENDENCIA,
					IE_TIPO_REGISTRO,
					DS_ITEM)
				SELECT nextval('pep_item_pendente_seq'),
					LOCALTIMESTAMP,
					coalesce(a_nm_usuario_w, wheb_usuario_pck.get_nm_usuario),
					LOCALTIMESTAMP,
					coalesce(a_nm_usuario_w, wheb_usuario_pck.get_nm_usuario),
					LOCALTIMESTAMP,
					cd_pessoa_fisica_w,
					nr_atendimento_w,
					nr_atendimento_w,
					'L',
					'DPC',
					obter_desc_expressao(1046875)  as DS_ITEM;
			END LOOP;
		CLOSE C01;

		end if;
	end if;
end if;	
end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_atnd_pacte_dpc_ins_upd_after() FROM PUBLIC;

CREATE TRIGGER atnd_pacte_dpc_ins_upd_after
	AFTER INSERT OR UPDATE ON atendimento_paciente FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_atnd_pacte_dpc_ins_upd_after();

