DROP TRIGGER IF EXISTS atualizar_estab_estr_org ON estabelecimento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_atualizar_estab_estr_org() RETURNS trigger AS $BODY$
BEGIN
if (Obter_Valor_Param_Usuario(911, 52, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo) = 'S') then
	BEGIN
	  if (NEW.ie_situacao = 'I') then
	   BEGIN
	     update estrutura_organizacional set IE_SITUACAO = NEW.IE_SITUACAO
		   where cd_estabelecimento = NEW.cd_estabelecimento;
	   end;
     end if;
	end;
end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_atualizar_estab_estr_org() FROM PUBLIC;

CREATE TRIGGER atualizar_estab_estr_org
	AFTER UPDATE ON estabelecimento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_atualizar_estab_estr_org();

