if (NEW.hr_quebra_noturno is not null) and
   ((NEW.hr_quebra_noturno <> OLD.hr_quebra_noturno) or (OLD.dt_quebra_noturno is null)) then
	NEW.dt_quebra_noturno := to_date(to_char(LOCALTIMESTAMP,'dd/mm/yyyy') || ' ' || NEW.hr_quebra_noturno || ':00:00', 'dd/mm/yyyy hh24:mi:ss');
end if;

RETURN NEW;
end
