SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS atual_funcao_macro_cliente ON funcao_macro_cliente CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_atual_funcao_macro_cliente() RETURNS trigger AS $BODY$
declare

pragma autonomous_transaction;

qt_macro_existe_w	bigint;

BEGIN

	select	count(*)
	into STRICT	qt_macro_existe_w
	from	funcao_macro_cliente
	where	ds_macro = NEW.ds_macro
	and		nr_sequencia <> NEW.nr_sequencia
	and		nr_seq_macro = NEW.nr_seq_macro
	and		upper(ds_locale) = upper(NEW.ds_locale)
	and		dt_inativacao is null;
	
	if (qt_macro_existe_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(724703);	
	end if;
	
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_atual_funcao_macro_cliente() FROM PUBLIC;

CREATE TRIGGER atual_funcao_macro_cliente
	BEFORE INSERT OR UPDATE ON funcao_macro_cliente FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_atual_funcao_macro_cliente();

