CREATE OR REPLACE FUNCTION trigger_fct_atual_regra_presc_agenda() RETURNS trigger AS $BODY$
declare

BEGIN

if (NEW.hr_agenda is not null) and
   ((NEW.hr_agenda <> OLD.hr_agenda) or (OLD.dt_agenda is null)) then
