CREATE OR REPLACE FUNCTION trigger_fct_atual_reg_consis_inf_rep() RETURNS trigger AS $BODY$
declare

BEGIN

if (NEW.hr_inicial is not null) and
   ((NEW.hr_inicial <> OLD.hr_inicial) or (OLD.dt_inicial is null)) then
