CREATE OR REPLACE FUNCTION trigger_fct_atual_setor_horario_atend() RETURNS trigger AS $BODY$
declare

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger	= 'S')  then

	if (NEW.DT_HORARIO is not null) then
		NEW.dt_horario := to_date('30/12/1899' || ' ' || to_char(NEW.dt_horario,'hh24:mi:ss'), 'dd/mm/yyyy hh24:mi:ss');
	end if;

	if (NEW.hr_limite is not null) and
	   ((NEW.hr_limite <> OLD.hr_limite) or (OLD.dt_limite is null)) then
		NEW.dt_limite := to_date(to_char(LOCALTIMESTAMP,'dd/mm/yyyy') || ' ' || NEW.hr_limite || ':00', 'dd/mm/yyyy hh24:mi:ss');
	end if;

end if;

RETURN NEW;
end;
$BODY$
