CREATE OR REPLACE FUNCTION trigger_fct_autorizacao_convenio_arq_aftup() RETURNS trigger AS $BODY$
declare
ds_titulo_w			varchar(255)	:= wheb_mensagem_pck.get_texto(312534);
ds_historico_w			varchar(4000)	:= '';
ds_texto_campo_w		varchar(20) := wheb_mensagem_pck.get_texto(182370);
ds_texto_antes_w		varchar(20) := wheb_mensagem_pck.get_texto(312535);
ds_texto_depois_w		varchar(20) := wheb_mensagem_pck.get_texto(182372);
BEGIN
  BEGIN

BEGIN
if (coalesce(NEW.ds_arquivo,0) <> coalesce(OLD.ds_arquivo,0)) then
	ds_historico_w := substr(ds_historico_w||ds_texto_campo_w|| ' DS_ARQUIVO - '||  ds_texto_antes_w || OLD.ds_arquivo ||' '||ds_texto_depois_w||' '|| NEW.ds_arquivo ||chr(13)||chr(10),1,4000);
end if;
if (coalesce(NEW.ie_anexar_email,0) <> coalesce(OLD.ie_anexar_email,0)) then
	ds_historico_w := substr(ds_historico_w||ds_texto_campo_w|| ' IE_ANEXAR_EMAIL - ' || ds_texto_antes_w || OLD.ie_anexar_email ||' '||ds_texto_Depois_w||' '|| NEW.ie_anexar_email ||chr(13)||chr(10),1,4000);
end if;
exception
when others then
	ds_historico_w := 'X';
end;

if (coalesce(ds_historico_w,'X') <> 'X') then
	CALL gravar_autor_conv_log_alter(NEW.nr_sequencia_autor,ds_titulo_w,substr(ds_historico_w,1,2000),NEW.nm_usuario);
end if;

  END;
RETURN NEW;
end
