DROP TRIGGER IF EXISTS autorizacao_convenio_hist_afup ON autorizacao_convenio_hist CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_autorizacao_convenio_hist_afup() RETURNS trigger AS $BODY$
declare
ds_titulo_w			varchar(255)	:= wheb_mensagem_pck.get_texto(312781);
ds_historico_w			varchar(4000)	:= '';
dt_parametro_w			timestamp		:= ESTABLISHMENT_TIMEZONE_UTILS.endOfDay(LOCALTIMESTAMP);
ds_antes_w			varchar(25)	:= wheb_mensagem_pck.get_texto(182371);
ds_depois_w			varchar(25)	:= wheb_mensagem_pck.get_texto(182372);
ds_campo_w			varchar(25)    := wheb_mensagem_pck.get_texto(182370);
BEGIN
  BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'N') then
    goto final;
end if;

BEGIN
if (coalesce(NEW.nr_atendimento,0) <> coalesce(OLD.nr_atendimento,0)) then
	ds_historico_w := substr(ds_historico_w||ds_campo_w || ' NR_ATENDIMENTO ' || ds_antes_w || ' ' || OLD.nr_atendimento ||' '|| ds_depois_w|| ' '|| NEW.nr_atendimento ||chr(13)||chr(10),1,4000);
end if;
if (coalesce(NEW.nr_seq_autorizacao,0) <> coalesce(OLD.nr_seq_autorizacao,0)) then
	ds_historico_w := substr(ds_historico_w||ds_campo_w || 'NR_SEQ_AUTORIZACAO ' || ds_antes_w || ' ' || OLD.nr_seq_autorizacao ||' '|| ds_depois_w|| ' '|| NEW.nr_seq_autorizacao ||chr(13)||chr(10),1,4000);
end if;
if (coalesce(NEW.ds_historico,0) <> coalesce(OLD.ds_historico,0)) then
	ds_historico_w := substr(ds_historico_w||ds_campo_w || 'DS_HISTORICO ' || ds_antes_w || ' ' || OLD.ds_historico ||' '|| ds_depois_w|| ' '|| NEW.ds_historico ||chr(13)||chr(10),1,4000);
end if;
if (coalesce(NEW.dt_liberacao,dt_parametro_w) <> coalesce(OLD.dt_liberacao,dt_parametro_w)) then
	ds_historico_w := substr(ds_historico_w||ds_campo_w || ' DT_LIBERACAO ' || ds_antes_w || ' ' || to_char(OLD.dt_liberacao,'dd/mm/yyyy hh24:mi:ss') ||' '|| ds_depois_w|| ' '|| to_char(NEW.dt_liberacao,'dd/mm/yyyy hh24:mi:ss') ||chr(13)||chr(10),1,4000);
end if;
if (coalesce(NEW.nm_usuario_lib,0) <> coalesce(OLD.nm_usuario_lib,0)) then
	ds_historico_w := substr(ds_historico_w||ds_campo_w || ' NM_USUARIO_LIB ' || ds_antes_w || ' ' || OLD.nm_usuario_lib ||' '|| ds_depois_w|| ' '|| NEW.nm_usuario_lib ||chr(13)||chr(10),1,4000);
end if;
if (coalesce(NEW.nr_seq_autor_origem,0) <> coalesce(OLD.nr_seq_autor_origem,0)) then
	ds_historico_w := substr(ds_historico_w||ds_campo_w || ' NR_SEQ_AUTOR_ORIGEM ' || ds_antes_w || ' ' || OLD.nr_seq_autor_origem ||' '|| ds_depois_w|| ' '|| NEW.nr_seq_autor_origem ||chr(13)||chr(10),1,4000);
end if;
if (coalesce(NEW.ie_visualiza_relat,0) <> coalesce(OLD.ie_visualiza_relat,0)) then
	ds_historico_w := substr(ds_historico_w||ds_campo_w || ' IE_VISUALIZA_RELAT ' || ds_antes_w || ' ' || OLD.ie_visualiza_relat ||' '|| ds_depois_w|| ' '|| NEW.ie_visualiza_relat ||chr(13)||chr(10),1,4000);
end if;
exception
when others then
	ds_historico_w := 'X';
end;

if (coalesce(ds_historico_w,'X') <> 'X') then
	CALL gravar_autor_conv_log_alter(NEW.nr_sequencia_autor,ds_titulo_w,substr(ds_historico_w,1,2000),NEW.nm_usuario);
end if;

<<final>>
null;

  END;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
