DROP TRIGGER IF EXISTS avf_pergunta_update ON avf_pergunta CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_avf_pergunta_update() RETURNS trigger AS $BODY$
declare
nr_seq_tipo_nota_item_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	avf_tipo_nota_item
	where	nr_seq_tipo_nota = NEW.nr_seq_tipo_nota;

BEGIN

if (NEW.nr_seq_tipo_nota <> OLD.nr_seq_tipo_nota) then
	BEGIN

	delete from avf_pergunta_tipo
	where	nr_seq_pergunta = NEW.nr_sequencia;

	open C01;
	loop
	fetch C01 into
		nr_seq_tipo_nota_item_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		BEGIN

		insert into avf_pergunta_tipo(	nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_pergunta,
				nr_seq_tipo_nota_item)
		values (	nextval('avf_pergunta_tipo_seq'),
				LOCALTIMESTAMP,
				NEW.nm_usuario,
				LOCALTIMESTAMP,
				NEW.nm_usuario_nrec,
				NEW.nr_sequencia,
				nr_seq_tipo_nota_item_w);
		end;
	end loop;
	close C01;

	end;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_avf_pergunta_update() FROM PUBLIC;

CREATE TRIGGER avf_pergunta_update
	AFTER UPDATE ON avf_pergunta FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_avf_pergunta_update();

