SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS banco_extrato_atual ON banco_extrato CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_banco_extrato_atual() RETURNS trigger AS $BODY$
declare

cd_estabelecimento_w	bigint;
ie_consistir_extrato_w	varchar(2);
qt_registro_w		bigint;

pragma autonomous_transaction;
BEGIN

select	max(a.cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from	banco_estabelecimento a
where	a.nr_sequencia = NEW.nr_seq_conta;

if (cd_estabelecimento_w is not null) then

	select	max(ie_consistir_extrato)
	into STRICT	ie_consistir_extrato_w
	from	parametro_controle_banc
	where	cd_estabelecimento = cd_estabelecimento_w;

	if (ie_consistir_extrato_w = 'S') then
		select 	count(*)
		into STRICT	qt_registro_w
		from	banco_extrato a
		where	trunc(a.dt_inicio,'dd') = trunc(NEW.dt_inicio,'dd')
		and	trunc(a.dt_final,'dd') = trunc(NEW.dt_final,'dd')
		and	a.nr_seq_conta = NEW.nr_seq_conta
		and	a.nr_sequencia <> NEW.nr_sequencia;

		if (qt_registro_w > 0) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(261498);
		end if;

	end if;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_banco_extrato_atual() FROM PUBLIC;

CREATE TRIGGER banco_extrato_atual
	BEFORE INSERT OR UPDATE ON banco_extrato FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_banco_extrato_atual();

