SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS bordero_pagamento_update ON bordero_pagamento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_bordero_pagamento_update() RETURNS trigger AS $BODY$
declare

qt_titulos_w bigint;

BEGIN

if (NEW.nr_seq_conta_banco <> OLD.nr_seq_conta_banco) and (coalesce(obter_valor_param_usuario(855,83, obter_perfil_ativo, NEW.nm_usuario, wheb_usuario_pck.get_cd_estabelecimento),'N') = 'N')  then

	select count(*)
	into STRICT qt_titulos_w
	from titulo_pagar a,
		 bordero_tit_pagar b
	where b.nr_bordero = OLD.nr_bordero
	and   b.nr_titulo = a.nr_titulo
	and   a.nr_seq_conta_bco is not null
	and   a.nr_seq_proj_rec  is not null
	and   a.nr_seq_conta_bco <> NEW.nr_seq_conta_banco;

	if (qt_titulos_w > 0)then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(553118);
	end if;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_bordero_pagamento_update() FROM PUBLIC;

CREATE TRIGGER bordero_pagamento_update
	BEFORE UPDATE ON bordero_pagamento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_bordero_pagamento_update();

