DROP TRIGGER IF EXISTS bsc_ind_inf_update ON bsc_ind_inf CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_bsc_ind_inf_update() RETURNS trigger AS $BODY$
declare

ie_regra_result_w	varchar(15);
nm_indicador_w	varchar(255);

BEGIN

select	coalesce(max(ie_regra_result),'MA'),
	max(nm_indicador)
into STRICT	ie_regra_result_w,
	nm_indicador_w
from	bsc_indicador
where	nr_sequencia = NEW.nr_seq_indicador;

if (ie_regra_result_w = 'MA') then
	BEGIN
	if (coalesce(NEW.qt_limite,0) > coalesce(NEW.qt_meta,0)) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(281869,'NR_SEQ_INDICADOR='||NEW.nr_seq_indicador ||';NM_INDICADOR='|| nm_indicador_w);
	end if;
	end;
elsif (ie_regra_result_w = 'ME') then
	BEGIN
	if (coalesce(NEW.qt_limite,0) < coalesce(NEW.qt_meta,0)) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(281870,'NR_SEQ_INDICADOR='||NEW.nr_seq_indicador ||';NM_INDICADOR='|| nm_indicador_w);
	end if;
	end;
end if;
RETURN NEW;
end
$BODY$
