DROP TRIGGER IF EXISTS caixa_atual ON caixa CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_caixa_atual() RETURNS trigger AS $BODY$
declare

ds_conta_contabil_w			varchar(40);

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
ds_conta_contabil_w		:= substr(wheb_mensagem_pck.get_texto(354087,''),1,40);

CALL ctb_consistir_conta_titulo(NEW.cd_conta_contabil, ds_conta_contabil_w);

ctb_consistir_conta_estab(NEW.cd_estabelecimento, NEW.cd_conta_contabil, ds_conta_contabil_w);
end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_caixa_atual() FROM PUBLIC;

CREATE TRIGGER caixa_atual
	BEFORE INSERT OR UPDATE ON caixa FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_caixa_atual();

