SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS can_ficha_admissao_insert ON can_ficha_admissao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_can_ficha_admissao_insert() RETURNS trigger AS $BODY$
declare
qt_registro_w	integer;

BEGIN

if (NEW.nr_prontuario is not null) then
	select	count(*)
	into STRICT	qt_registro_w
	from	can_ficha_admissao
	where	cd_pessoa_fisica	= NEW.cd_pessoa_fisica
	and	nr_registro		= NEW.nr_registro;
	
	if (qt_registro_w > 0) then
		--Ja existe um item cadastrado com esse numero de prontuario e registro de tumor.');

		CALL Wheb_mensagem_pck.exibir_mensagem_abort(261563);
	end if;

	select	count(*)
	into STRICT	qt_registro_w
	from	can_ficha_admissao
	where	cd_pessoa_fisica		= NEW.cd_pessoa_fisica
	and	ie_mais_um_tu_prim		= 2
	and	ESTABLISHMENT_TIMEZONE_UTILS.startOfYear(dt_preench_ficha)	= ESTABLISHMENT_TIMEZONE_UTILS.startOfYear(NEW.dt_preench_ficha);	

	
	if (qt_registro_w > 0) then
		--Existe um registro cadastrado para esse paciente, onde o campo Mais de um tumor esta como nao.');

		CALL Wheb_mensagem_pck.exibir_mensagem_abort(261564);
	end if;

end if;

RETURN NEW;
END;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_can_ficha_admissao_insert() FROM PUBLIC;

CREATE TRIGGER can_ficha_admissao_insert
	BEFORE INSERT ON can_ficha_admissao FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_can_ficha_admissao_insert();

