SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS can_ficha_admissao_update ON can_ficha_admissao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_can_ficha_admissao_update() RETURNS trigger AS $BODY$
declare
qt_reg_w	smallint;
ie_atualiza_cadastro_w	varchar(01);

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

select	coalesce(vl_parametro, vl_parametro_padrao)
into STRICT	ie_atualiza_cadastro_w
from	funcao_parametro
where	cd_funcao	= 3131
and	nr_sequencia	= 1;


if (ie_atualiza_cadastro_w	= 'S')		and (NEW.dt_obito 		is not null)	and (OLD.dt_obito		is null)	then
	update	pessoa_fisica
	set	dt_obito		= NEW.dt_obito
	where	cd_pessoa_fisica	= NEW.cd_pessoa_fisica;
end if;
<<Final>>
qt_reg_w	:= 0;
RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_can_ficha_admissao_update() FROM PUBLIC;

CREATE TRIGGER can_ficha_admissao_update
	BEFORE UPDATE ON can_ficha_admissao FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_can_ficha_admissao_update();

