CREATE OR REPLACE FUNCTION trigger_fct_can_ficha_seguimento_update() RETURNS trigger AS $BODY$
declare

ie_atualiza_cadastro_w	varchar(01);
qt_reg_w	smallint;

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

if (NEW.CD_TOPOGRAFIA_RECIDIVA1 is null) and (NEW.DT_RECIDIVA_1 is not null) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(278776);
elsif (NEW.CD_TOPOGRAFIA_RECIDIVA2 is null) and (NEW.DT_RECIDIVA_2 is not null) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(278777);
elsif (NEW.CD_TOPOGRAFIA_RECIDIVA3 is null) and (NEW.DT_RECIDIVA_3 is not null) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(278778);
elsif (NEW.CD_TOPOGRAFIA_RECIDIVA4 is null) and (NEW.DT_RECIDIVA_4 is not null) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(278779);
end if;

if	(NEW.CD_TOPOGRAFIA_RECIDIVA1 is not null AND NEW.DT_RECIDIVA_1 is null) or
	(NEW.CD_TOPOGRAFIA_RECIDIVA2 is not null AND NEW.DT_RECIDIVA_2 is null) or
	(NEW.CD_TOPOGRAFIA_RECIDIVA3 is not null AND NEW.DT_RECIDIVA_3 is null) or
	(NEW.CD_TOPOGRAFIA_RECIDIVA4 is not null AND NEW.DT_RECIDIVA_4 is null) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(278780);
end if;

select	coalesce(vl_parametro, vl_parametro_padrao)
into STRICT	ie_atualiza_cadastro_w
from	funcao_parametro
where	cd_funcao	= 3131
and	nr_sequencia	= 1;


if (ie_atualiza_cadastro_w	= 'S')		and (NEW.dt_obito 		is not null)	and (OLD.dt_obito		is null)	then
	update	pessoa_fisica
	set	dt_obito		= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(NEW.dt_obito)
	where	cd_pessoa_fisica	= NEW.cd_pessoa_fisica;
end if;

<<Final>>
qt_reg_w	:= 0;
RETURN NEW;
END;
