SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS can_ordem_prod_insert ON can_ordem_prod CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_can_ordem_prod_insert() RETURNS trigger AS $BODY$
declare
nr_atendimento_w	bigint;
ds_origem_w		varchar(4000);
cd_pessoa_fisica_atend_w			pessoa_fisica.cd_pessoa_fisica%type;

BEGIN

if (NEW.nr_prescricao is not null) then
	select	coalesce(max(nr_atendimento),0)
	into STRICT	nr_atendimento_w
	from	prescr_medica
	where	nr_prescricao	= NEW.nr_prescricao;


	if (nr_atendimento_w > 0) then
		NEW.nr_atendimento	:= nr_atendimento_w;
	end if;
end if;

	NEW.ds_stack_origem := substr(dbms_utility.format_call_stack,1,4000);

if (NEW.DT_GERACAO_ORDEM is null) then
	NEW.DT_GERACAO_ORDEM := LOCALTIMESTAMP;
end if;

if (NEW.nr_atendimento is not null) and (NEW.cd_pessoa_fisica is not null) then

	select  max(cd_pessoa_fisica)
	into STRICT	cd_pessoa_fisica_atend_w
	from 	atendimento_paciente
	where	nr_atendimento = NEW.nr_atendimento;

	if (NEW.cd_pessoa_fisica <> coalesce(cd_pessoa_fisica_atend_w,NEW.cd_pessoa_fisica)) then
		CALL wheb_mensagem_pck.Exibir_Mensagem_Abort(1005227);
	end if;
end if;

RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_can_ordem_prod_insert() FROM PUBLIC;

CREATE TRIGGER can_ordem_prod_insert
	BEFORE INSERT ON can_ordem_prod FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_can_ordem_prod_insert();

