SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS centro_custo_atual ON centro_custo CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_centro_custo_atual() RETURNS trigger AS $BODY$
declare

qt_registro_w   bigint;

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger       = 'S')  then

if (NEW.dt_fim_contabil is not null) then
BEGIN
        select  count(*)
        into STRICT    qt_registro_w
        from    ctb_saldo a,
                ctb_mes_ref b
        where   a.nr_seq_mes_ref = b.nr_sequencia
        and     b.dt_referencia >= NEW.dt_fim_contabil
        and     a.cd_centro_custo = NEW.cd_centro_custo
        and     a.vl_saldo <> 0;

        if (qt_registro_w <> 0) then
                CALL wheb_mensagem_pck.exibir_mensagem_abort(1160571, 'DT_FIM_CONTABIL ='||NEW.dt_fim_contabil);
        end if;
end;
end if;
end if;

RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_centro_custo_atual() FROM PUBLIC;

CREATE TRIGGER centro_custo_atual
	BEFORE INSERT OR UPDATE ON centro_custo FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_centro_custo_atual();

