CREATE OR REPLACE FUNCTION trigger_fct_checkup_dado_estat_atual() RETURNS trigger AS $BODY$
declare

BEGIN
if (NEW.cd_paciente is null) and (NEW.nr_atendimento > 0) then
	select	cd_pessoa_fisica
	into STRICT	NEW.cd_paciente
	from	atendimento_paciente
	where	nr_atendimento	= NEW.nr_atendimento;
end if;

RETURN NEW;
end
$BODY$
