DROP TRIGGER IF EXISTS checkup_protocolo_item_befins ON checkup_protocolo_item CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_checkup_protocolo_item_befins() RETURNS trigger AS $BODY$
declare
nr_seq_apresentacao_w	smallint;

BEGIN

select	coalesce(max(nr_seq_apres),0)
into STRICT	nr_seq_apresentacao_w
from	checkup_tipo_dado_estat
where	nr_sequencia = NEW.nr_seq_dado;

if (coalesce(nr_seq_apresentacao_w,0) > 0) and (NEW.nr_seq_apresent = 100) then
	NEW.nr_seq_apresent := nr_seq_apresentacao_w;
end if;

RETURN NEW;
END
