SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS cheque_atual ON cheque CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_cheque_atual() RETURNS trigger AS $BODY$
declare
ie_data_comp_cheque_w	varchar(255);

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
select	coalesce(max(ie_data_comp_cheque), 'S')
into STRICT	ie_data_comp_cheque_w
from	parametros_contas_pagar
where	cd_estabelecimento		= NEW.cd_estabelecimento;

if (ie_data_comp_cheque_w = 'N') and (NEW.dt_compensacao is not null) and (NEW.dt_emissao is not null) and (NEW.dt_compensacao <> coalesce(OLD.dt_compensacao, NEW.dt_compensacao - 1)) and (trunc(NEW.dt_compensacao, 'dd') < trunc(NEW.dt_emissao, 'dd')) then
	-- A data de compensacao do cheque deve ser maior ou igual a data de emissao do mesmo!

	CALL wheb_mensagem_pck.exibir_mensagem_abort(266911);
end if;
end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_cheque_atual() FROM PUBLIC;

CREATE TRIGGER cheque_atual
	BEFORE INSERT OR UPDATE ON cheque FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_cheque_atual();

