SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS cheque_cr_delete ON cheque_cr CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_cheque_cr_delete() RETURNS trigger AS $BODY$
declare

dt_fechamento_w	timestamp	:= null;
nr_recibo_w	bigint;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
if (OLD.nr_seq_caixa_rec is not null) then
	select	max(dt_fechamento),
		max(nr_recibo)
	into STRICT	dt_fechamento_w,
		nr_recibo_w
	from	caixa_receb
	where	nr_sequencia	= OLD.nr_seq_caixa_rec;

	if (dt_fechamento_w is not null) then
		/* Nao e possivel excluir esse cheque!
		O recibo #@NR_RECIBO#@ ja foi fechado! */

		CALL wheb_mensagem_pck.exibir_mensagem_abort(267126, 'NR_RECIBO=' || nr_recibo_w);
	end if;
end if;

/* Grava o agendamento da informacao para atualizacao do fluxo de caixa. */


CALL gravar_agend_fluxo_caixa(OLD.nr_seq_cheque,null,'CR',coalesce(OLD.dt_vencimento_atual,OLD.dt_vencimento),'I',OLD.nm_usuario);

end if;
RETURN OLD;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_cheque_cr_delete() FROM PUBLIC;

CREATE TRIGGER cheque_cr_delete
	BEFORE DELETE ON cheque_cr FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_cheque_cr_delete();

