SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS ciap_atendimento_atual ON ciap_atendimento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_ciap_atendimento_atual() RETURNS trigger AS $BODY$
declare

nr_seq_sintoma_w  	 problema_ciap.nr_sequencia%type;

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger <> 'N') then
  if (coalesce(NEW.cd_ciap,0) <> coalesce(OLD.cd_ciap,0)) then
    select	coalesce(max(nr_sequencia),0)
    into STRICT	nr_seq_sintoma_w
    from	problema_ciap
    where	cd_ciap	= NEW.cd_ciap;

    if (nr_seq_sintoma_w > 0) then
      NEW.nr_seq_sintoma := nr_seq_sintoma_w;
    else
      CALL wheb_mensagem_pck.exibir_mensagem_abort(851899);
    end if;
  end if;

  if (coalesce(OLD.DT_REGISTRO,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_REGISTRO) and (NEW.DT_REGISTRO is not null) then
    NEW.ds_utc		:= obter_data_utc(NEW.DT_REGISTRO, 'HV');	
  end if;

  if (coalesce(OLD.DT_LIBERACAO,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_LIBERACAO) and (NEW.DT_LIBERACAO is not null) then
    NEW.ds_utc_atualizacao	:= obter_data_utc(NEW.DT_LIBERACAO,'HV');
  end if;
end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_ciap_atendimento_atual() FROM PUBLIC;

CREATE TRIGGER ciap_atendimento_atual
	BEFORE INSERT OR UPDATE ON ciap_atendimento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_ciap_atendimento_atual();

