C01 CURSOR FOR 
	SELECT	a.nr_seq_evento 
	from	regra_envio_sms a 
	where	a.ie_evento_disp	= 'DI' 
	and	coalesce(a.ie_situacao,'A') = 'A';
 
 
BEGIN 
 
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N') then 
	goto Final;
end if;
 
 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_evento_medico_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	BEGIN 
	if 	((NEW.IE_GERA_ALERTA <> OLD.IE_GERA_ALERTA) or (OLD.IE_GERA_ALERTA is null )) and (NEW.IE_GERA_ALERTA = 'S') then 
		CALL gerar_evento_medico_infecto(nr_seq_evento_medico_w,NEW.NR_ATENDIMENTO,null,NEW.NR_PRESCRICAO,NEW.nm_usuario,null);
	end if;
	 
	end;
end loop;
close C01;
 
 
 
<<Final>> 
nr_seq_evento_medico_w	:= 0;
 
RETURN NEW;
end
