CREATE OR REPLACE FUNCTION trigger_fct_cih_local_infeccao_befins() RETURNS trigger AS $BODY$
declare
 
cd_caso_infeccao_w		bigint;
nr_seq_evento_infeccao_w bigint;
nr_atendimento_w		bigint;
cd_estabelecimento_w	bigint;
 
c01 CURSOR FOR 
SELECT	cd_caso_infeccao, 
		nr_seq_evento_infeccao 
from	cih_parametros;	
 
BEGIN 
 
OPEN C01;
LOOP 
FETCH C01 into 
	cd_caso_infeccao_w, 
	nr_seq_evento_infeccao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
 
if (cd_caso_infeccao_w is not null) then 
 
	if (NEW.cd_caso_infeccao = cd_caso_infeccao_w) then 
	 
		select	a.nr_atendimento, 
				a.cd_estabelecimento 
		into STRICT	nr_atendimento_w, 
				cd_estabelecimento_w 
		from	atendimento_paciente a, 
				cih_ficha_ocorrencia c 
		where	c.nr_atendimento	= a.nr_atendimento 
		and		c.nr_ficha_ocorrencia	= NEW.nr_ficha_ocorrencia;
			 
		CALL gerar_evento_infeccao_trig(NEW.nm_usuario, nr_atendimento_w, cd_estabelecimento_w, NEW.cd_topografia, NEW.cd_caso_infeccao, NEW.cd_clinica, nr_seq_evento_infeccao_w);
	 
	end if;
 
end if;
END LOOP;
CLOSE C01;
 
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_cih_local_infeccao_befins() FROM PUBLIC;

CREATE TRIGGER cih_local_infeccao_befins
	BEFORE INSERT ON cih_local_infeccao FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_cih_local_infeccao_befins();

