SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS cirurgia_atual ON cirurgia CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_cirurgia_atual() RETURNS trigger AS $BODY$
declare

ie_clinica_w			atendimento_paciente.ie_clinica%type;

BEGIN
	if (coalesce(OLD.DT_INICIO_PREVISTA,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_INICIO_PREVISTA) and (NEW.DT_INICIO_PREVISTA is not null) then
		NEW.ds_utc		:= obter_data_utc(NEW.DT_INICIO_PREVISTA, 'HV');
	end if;

	if (coalesce(OLD.DT_LIBERACAO,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_LIBERACAO) and (NEW.DT_LIBERACAO is not null) then
		NEW.ds_utc_atualizacao	:= obter_data_utc(NEW.DT_LIBERACAO,'HV');
	end if;

	if (TG_OP = 'UPDATE') then
		if (coalesce((pkg_i18n.get_user_locale), 'pt_BR') = 'es_MX') and (OLD.dt_fim_cirurgia is null) and (NEW.dt_fim_cirurgia is not null) then

			select 	max(ie_clinica)
			into STRICT 	ie_clinica_w
			from 	atendimento_paciente
			where nr_atendimento = NEW.nr_atendimento;

			CALL gerar_codificacao_atendimento(
				NEW.nr_atendimento,
				ie_clinica_w,
				NEW.nm_usuario,
				null,
				null,
				NEW.cd_procedimento_princ,
				NEW.ie_origem_proced,
				'N',
				NEW.nr_cirurgia);
		end if;
	end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_cirurgia_atual() FROM PUBLIC;

CREATE TRIGGER cirurgia_atual
	BEFORE INSERT OR UPDATE ON cirurgia FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_cirurgia_atual();

