SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS cirurgia_descricao_atual ON cirurgia_descricao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_cirurgia_descricao_atual() RETURNS trigger AS $BODY$
DECLARE

cd_pessoa_fisica_w		pessoa_fisica.cd_pessoa_fisica%type;
nr_atendimento_w		atendimento_paciente.nr_atendimento%type;
ie_tipo_w			varchar(10);
ie_lib_desc_cirurgia	varchar(5);

BEGIN
ie_lib_desc_cirurgia := obter_param_usuario(872, 7, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, obter_estabelecimento_ativo, ie_lib_desc_cirurgia);

if (ie_lib_desc_cirurgia = 'S') then
	if (NEW.nr_seq_pepo > 0) then
		select	max(c.nr_atendimento),
				max(c.cd_pessoa_fisica)
		into STRICT	nr_atendimento_w,
				cd_pessoa_fisica_w
		from	pepo_cirurgia c
		where	c.nr_sequencia = NEW.nr_seq_pepo;

	else
		select	max(c.nr_atendimento),
				max(c.cd_pessoa_fisica)
		into STRICT	nr_atendimento_w,
				cd_pessoa_fisica_w
		from	cirurgia c
		where	c.nr_cirurgia = NEW.nr_cirurgia;

	end if;

	if (NEW.dt_liberacao is null) then
		ie_tipo_w := 'CDC';
	elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
		ie_tipo_w := 'XCDC';
	end if;

	if (ie_tipo_w is not null) then
		CALL Gerar_registro_pendente_PEP(ie_tipo_w, NEW.nr_sequencia, cd_pessoa_fisica_w, nr_atendimento_w, NEW.nm_usuario);
	end if;
end if;

RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_cirurgia_descricao_atual() FROM PUBLIC;

CREATE TRIGGER cirurgia_descricao_atual
	AFTER INSERT OR UPDATE ON cirurgia_descricao FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_cirurgia_descricao_atual();

