CREATE OR REPLACE FUNCTION trigger_fct_cirurgia_descricao_update() RETURNS trigger AS $BODY$
DECLARE

qt_reg_w bigint;

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

if	(OLD.dt_inativacao is null AND NEW.dt_inativacao is not null) and
	(OLD.nm_usuario_inativacao is null AND NEW.nm_usuario_inativacao is not null) and (NEW.cd_evolucao is not null) then
	update	evolucao_paciente
	set	dt_inativacao 		= LOCALTIMESTAMP,
		nm_usuario_inativacao	= NEW.nm_usuario_inativacao,
		ie_situacao		= 'I',
		ds_justificativa	= NEW.ds_justificativa
	where	cd_evolucao		= NEW.cd_evolucao
	and	dt_inativacao 		is null
	and	dt_liberacao		is not null;

	delete  FROM pep_item_pendente
	where 	cd_evolucao 		= NEW.cd_evolucao;

end if;



<<Final>>

qt_reg_w	:= 0;


RETURN NEW;
END
$BODY$
