SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS cirurgia_evento_adic_update ON cirurgia_evento_adic CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_cirurgia_evento_adic_update() RETURNS trigger AS $BODY$
declare

ie_iniciar_equipamento_w	cirurgia_evento_adic_regra.ie_iniciar_equipamento%type;

BEGIN

	select 	coalesce(max(reg.ie_iniciar_equipamento),'N')
	into STRICT	ie_iniciar_equipamento_w
	from	cirurgia_evento_adic_regra reg
	where	reg.nr_sequencia = OLD.nr_seq_tipo_evento;

	if (ie_iniciar_equipamento_w = 'S' and NEW.dt_inativacao is not null) then
		update   equipamento_cirurgia
		set      dt_inicio  = NULL
		where    (nr_cirurgia = OLD.nr_cirurgia AND OLD.nr_cirurgia is not null)
		and      dt_inicio = OLD.dt_liberacao
		and      dt_liberacao is null;
	end if;
	
	if (ie_iniciar_equipamento_w = 'S' and NEW.dt_liberacao is not null and NEW.dt_inativacao is null) then
		update   equipamento_cirurgia
		set      dt_inicio = NEW.dt_liberacao
		where    (nr_cirurgia = OLD.nr_cirurgia AND OLD.nr_cirurgia is not null)
		and      dt_inicio = OLD.dt_inicio_evento
		and      dt_liberacao is null;
	end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_cirurgia_evento_adic_update() FROM PUBLIC;

CREATE TRIGGER cirurgia_evento_adic_update
	AFTER UPDATE ON cirurgia_evento_adic FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_cirurgia_evento_adic_update();

