SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS cirurgia_item_cont_pend_atual ON cirurgia_item_controle CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_cirurgia_item_cont_pend_atual() RETURNS trigger AS $BODY$
declare

qt_reg_w			smallint;
ie_tipo_w			varchar(10);
ie_libera_cavidade_w		varchar(10);
cd_pessoa_fisica_w		varchar(10);
nr_atendimento_w	bigint;

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

select	max(ie_libera_cavidade)
into STRICT	ie_libera_cavidade_w
from	parametro_medico
where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

if (coalesce(ie_libera_cavidade_w,'N') = 'S') then
	select	max(c.nr_atendimento),
				max(c.cd_pessoa_fisica)
	into STRICT		nr_atendimento_w,
				cd_pessoa_fisica_w
	from		cirurgia c
	where		c.nr_cirurgia = NEW.nr_cirurgia;

	if (cd_pessoa_fisica_w is null) then
		select	max(c.nr_atendimento),
					max(c.cd_pessoa_fisica)
		into STRICT		nr_atendimento_w,
					cd_pessoa_fisica_w
		from		pepo_cirurgia c
		where		c.nr_sequencia = NEW.nr_seq_pepo;
	end if;

	if (NEW.dt_liberacao is null) then
		ie_tipo_w := 'LCC';
	elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
		ie_tipo_w := 'XLCC';
	end if;

	if (ie_tipo_w	is not null) then
		CALL Gerar_registro_pendente_PEP(ie_tipo_w, NEW.nr_sequencia, cd_pessoa_fisica_w, nr_atendimento_w, NEW.nm_usuario);
	end if;
end if;

<<Final>>
qt_reg_w	:= 0;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_cirurgia_item_cont_pend_atual() FROM PUBLIC;

CREATE TRIGGER cirurgia_item_cont_pend_atual
	AFTER INSERT OR UPDATE ON cirurgia_item_controle FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_cirurgia_item_cont_pend_atual();

