SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS cirurgia_tec_anest_pend_atual ON cirurgia_tec_anestesica CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_cirurgia_tec_anest_pend_atual() RETURNS trigger AS $BODY$
declare

qt_reg_w		smallint;
ie_tipo_w		varchar(10);
cd_pessoa_fisica_w		varchar(10);
nr_atendimento_w		bigint;
ie_libera_anestesia_w	varchar(15);

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

select	coalesce(max(ie_libera_anestesia),'N')
into STRICT		ie_libera_anestesia_w
from		parametro_medico
where		cd_estabelecimento = obter_estabelecimento_ativo;

if (ie_libera_anestesia_w = 'S') then
	select	max(c.nr_atendimento),
		max(c.cd_pessoa_fisica)
	into STRICT	nr_atendimento_w,
		cd_pessoa_fisica_w
	from	cirurgia c
	where	c.nr_cirurgia = NEW.nr_cirurgia;

	if (cd_pessoa_fisica_w is null) then
		select	max(c.nr_atendimento),
			max(c.cd_pessoa_fisica)
		into STRICT	nr_atendimento_w,
			cd_pessoa_fisica_w
		from	pepo_cirurgia c
		where	c.nr_sequencia = NEW.nr_seq_pepo;
	end if;

	if (NEW.dt_liberacao is null) then
		ie_tipo_w := 'CTA';
	elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
		ie_tipo_w := 'XCTA';
	end if;

	if (ie_tipo_w	is not null) then
		CALL Gerar_registro_pendente_PEP(ie_tipo_w, NEW.nr_sequencia, cd_pessoa_fisica_w, nr_atendimento_w, NEW.nm_usuario,null, null, null, null, null, null, null);
	end if;
end if;

<<Final>>
qt_reg_w	:= 0;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_cirurgia_tec_anest_pend_atual() FROM PUBLIC;

CREATE TRIGGER cirurgia_tec_anest_pend_atual
	AFTER INSERT OR UPDATE ON cirurgia_tec_anestesica FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_cirurgia_tec_anest_pend_atual();

