SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS cirur_agente_anest_pend_atual ON cirurgia_agente_anestesico CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_cirur_agente_anest_pend_atual() RETURNS trigger AS $BODY$
declare

qt_reg_w			smallint;
ie_tipo_w			varchar(10);
ie_libera_medic_pepo_w		varchar(1);
ie_libera_material_pepo_w	varchar(1);
ie_libera_agente_pepo_w		varchar(1);
ie_libera_hemoder_pepo_w	varchar(1);
ie_libera_terapia_pepo_w	varchar(1);
cd_pessoa_fisica_w		varchar(10);
nr_atendimento_w		bigint;
nr_seq_item_pepo_w		bigint;
nr_seq_atend_cons_pepa_w cirurgia.nr_seq_atend_cons_pepa%type;
BEGIN
  BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

select	max(ie_libera_medic_pepo),
	max(ie_libera_material_pepo),
	max(ie_libera_agente_pepo),
	max(ie_libera_hemoder_pepo),
	max(ie_libera_terapia_pepo)
into STRICT	ie_libera_medic_pepo_w,
	ie_libera_material_pepo_w,
	ie_libera_agente_pepo_w,
	ie_libera_hemoder_pepo_w,
	ie_libera_terapia_pepo_w
from	parametro_medico
where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

if (NEW.ie_tipo = 1) and (coalesce(ie_libera_agente_pepo_w,'N') = 'S') then --Agentes anestesicos	
	if (NEW.dt_liberacao is null) then
		ie_tipo_w := 'CAAA';
	elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
		ie_tipo_w := 'XCAAA';
	end if;
elsif (NEW.ie_tipo = 2) and (coalesce(ie_libera_terapia_pepo_w,'N') = 'S') then --Terapia hidroeletrolitica		
	if (NEW.dt_liberacao is null) then
		ie_tipo_w := 'CAAT';
	elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
		ie_tipo_w := 'XCAAT';
	end if;
elsif (NEW.ie_tipo = 3) and (coalesce(ie_libera_medic_pepo_w,'N') = 'S') then --Medicamentos
	if (NEW.dt_liberacao is null) then
		ie_tipo_w := 'CAAM';
	elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
		ie_tipo_w := 'XCAAM';
	end if;
elsif (NEW.ie_tipo = 4) and (coalesce(ie_libera_material_pepo_w,'N') = 'S') then --Materiais
	if (NEW.dt_liberacao is null) then
		ie_tipo_w := 'CAAMAT';
	elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
		ie_tipo_w := 'XCAAMAT';
	end if;
elsif (NEW.ie_tipo = 5) and (coalesce(ie_libera_hemoder_pepo_w,'N') = 'S') then --Hemocomponente
	if (NEW.dt_liberacao is null) then
		ie_tipo_w := 'CAAH';
	elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
		ie_tipo_w := 'XCAAH';
	end if;
end if;	

BEGIN	
if (ie_tipo_w	is not null) then
		
	select	max(c.nr_atendimento),
		max(c.cd_pessoa_fisica)
	into STRICT	nr_atendimento_w,
		cd_pessoa_fisica_w
	from	cirurgia c
	where	c.nr_cirurgia = NEW.nr_cirurgia;

	if (cd_pessoa_fisica_w is null) then
		select	max(c.nr_atendimento),
			max(c.cd_pessoa_fisica)
		into STRICT	nr_atendimento_w,
			cd_pessoa_fisica_w
		from	pepo_cirurgia c
		where	c.nr_sequencia = NEW.nr_seq_pepo;
	end if;
	
  select max(b.nr_seq_atend_cons_pepa)
    into STRICT nr_seq_atend_cons_pepa_w
    from cirurgia b
    where	b.nr_cirurgia = NEW.nr_cirurgia;
  CALL Gerar_registro_pendente_PEP(
      cd_tipo_registro_p => ie_tipo_w,
      nr_sequencia_registro_p => NEW.nr_sequencia,
      cd_pessoa_fisica_p => cd_pessoa_fisica_w,
      nr_atendimento_p => nr_atendimento_w, 
      nm_usuario_p => NEW.nm_usuario,
      nr_atend_cons_pepa_p=> nr_seq_atend_cons_pepa_w
    );
end if;
exception
	when others then
	null;
end;
	
<<Final>>
qt_reg_w	:= 0;
  END;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_cirur_agente_anest_pend_atual() FROM PUBLIC;

CREATE TRIGGER cirur_agente_anest_pend_atual
	AFTER INSERT OR UPDATE ON cirurgia_agente_anestesico FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_cirur_agente_anest_pend_atual();

