SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS classif_equipamento_update ON classif_equipamento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_classif_equipamento_update() RETURNS trigger AS $BODY$
declare
qt_equip_inativos_w	bigint;

BEGIN

if (NEW.ie_situacao = 'I') AND (NEW.ie_situacao <> OLD.ie_situacao ) then

	select 	count(*)
	into STRICT	qt_equip_inativos_w
	from	equipamento
	where	ie_situacao = 'A'
	and	CD_CLASSIFICACAO = NEW.nr_sequencia;

	if (qt_equip_inativos_w > 0) then
		--Não é possível inativar a classificação do equipamento pois existem equipamentos ativos para esta classificação#@#@
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(263505);
	end if;

end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_classif_equipamento_update() FROM PUBLIC;

CREATE TRIGGER classif_equipamento_update
	BEFORE INSERT OR UPDATE ON classif_equipamento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_classif_equipamento_update();

