SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS cm_ciclo_after ON cm_ciclo CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_cm_ciclo_after() RETURNS trigger AS $BODY$
declare

gerar_ceq_cod_ciclo_w	varchar(2);

BEGIN

if (TG_OP = 'INSERT') then

	gerar_ceq_cod_ciclo_w := Obter_Param_Usuario(406, 48, obter_perfil_ativo, NEW.nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, gerar_ceq_cod_ciclo_w);

	CALL gravar_log_tasy(675, 'Valor parâmetro [48]: ' || gerar_ceq_cod_ciclo_w ||
				' - Seq ciclo: ' || NEW.nr_sequencia ||
				' - Equipamento: ' || NEW.nr_seq_equipamento ||
				' - Ciclo: ' || NEW.nr_ciclo ||
				' - Lote: ' || NEW.cd_ciclo ||
				' - Controle: ' || NEW.nr_ciclo_controle ||
				' - Observação: ' || NEW.ds_observacao ||
				' - Data Inicio: ' || NEW.dt_inicio ||
				' - Data fim: ' || NEW.dt_fim, NEW.nm_usuario);

elsif (TG_OP = 'UPDATE') then

	gerar_ceq_cod_ciclo_w := Obter_Param_Usuario(406, 48, obter_perfil_ativo, NEW.nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, gerar_ceq_cod_ciclo_w);

	CALL gravar_log_tasy(675, 'Valor parâmetro [48]: ' || gerar_ceq_cod_ciclo_w ||
				' - Seq ciclo: ' || NEW.nr_sequencia ||
				' - Equipamento antigo: ' || OLD.nr_seq_equipamento ||
				' - Equipamento novo: ' || NEW.nr_seq_equipamento ||
				' - Ciclo antigo: ' || OLD.nr_ciclo ||
				' - Ciclo novo: ' || NEW.nr_ciclo ||
				' - Lote antigo: ' || OLD.cd_ciclo ||
				' - Lote novo: ' || NEW.cd_ciclo ||
				' - Controle antigo: ' || OLD.nr_ciclo_controle ||
				' - Controle novo: ' || NEW.nr_ciclo_controle ||
				' - Observação antigo: ' || OLD.ds_observacao ||
				' - Observação novo: ' || NEW.ds_observacao ||
				' - Data Inicio antigo: ' || OLD.dt_inicio ||
				' - Data Inicio novo: ' || NEW.dt_inicio ||
				' - Data fim antigo: ' || OLD.dt_fim ||
				' - Data fim novo: ' || NEW.dt_fim, NEW.nm_usuario);

	if (OLD.nr_seq_equipamento <> NEW.nr_seq_equipamento) then

		CALL cm_gerar_hist_ciclo(	wheb_mensagem_pck.get_texto(311186,'NR_SEQ_EQUIPAMENTO='||OLD.nr_seq_equipamento||';NR_SEQ_EQUIPAMENTO_NEW='||NEW.nr_seq_equipamento),
					'A',
					NEW.nr_sequencia,
					null,
					NEW.nm_usuario);

	end if;

	if (OLD.dt_fim is not null) and (NEW.dt_fim is not null) then

		if (OLD.nr_seq_classif <> NEW.nr_seq_classif) then

			CALL cm_gerar_hist_ciclo(	wheb_mensagem_pck.get_texto(311187,'NR_SEQ_CLASSIF='||OLD.nr_seq_classif||';NR_SEQ_CLASSIF_NEW='||NEW.nr_seq_classif),
						'A',
						NEW.nr_sequencia,
						null,
						NEW.nm_usuario);

		end if;

		if (OLD.nr_ciclo <> NEW.nr_ciclo) then

			CALL cm_gerar_hist_ciclo(	wheb_mensagem_pck.get_texto(311191,'NR_CICLO='||OLD.nr_ciclo||';NR_CICLO_NEW='||NEW.nr_ciclo),
						'A',
						NEW.nr_sequencia,
						null,
						NEW.nm_usuario);

		end if;

		if (OLD.cd_ciclo <> NEW.cd_ciclo) then

			CALL cm_gerar_hist_ciclo(	wheb_mensagem_pck.get_texto(311192,'CD_CICLO='||OLD.cd_ciclo||';CD_CICLO_NEW='||NEW.cd_ciclo),
						'A',
						NEW.nr_sequencia,
						null,
						NEW.nm_usuario);

		end if;

		if (OLD.qt_temperatura <> NEW.qt_temperatura) then

			CALL cm_gerar_hist_ciclo(	wheb_mensagem_pck.get_texto(311195,'QT_TEMPERATURA='||OLD.qt_temperatura||';QT_TEMPERATURA_NEW='||NEW.qt_temperatura),
						'A',
						NEW.nr_sequencia,
						null,
						NEW.nm_usuario);

		end if;

		if (OLD.ds_observacao <> NEW.ds_observacao) then

			CALL cm_gerar_hist_ciclo(	wheb_mensagem_pck.get_texto(311196,'DS_OBSERVACAO='||OLD.ds_observacao||';DS_OBSERVACAO_NEW='||NEW.ds_observacao),
						'A',
						NEW.nr_sequencia,
						null,
						NEW.nm_usuario);

		end if;

		if (OLD.qt_pressao <> NEW.qt_pressao) then

			CALL cm_gerar_hist_ciclo(	wheb_mensagem_pck.get_texto(311197,'QT_PRESSAO='||OLD.qt_pressao||';QT_PRESSAO_NEW='||NEW.qt_pressao),
						'A',
						NEW.nr_sequencia,
						null,
						NEW.nm_usuario);

		end if;

		if (OLD.ie_pressao_alto_nivel <> NEW.ie_pressao_alto_nivel) then

			CALL cm_gerar_hist_ciclo(	wheb_mensagem_pck.get_texto(311199,'IE_PRESSAO_ALTO_NIVEL='||OLD.ie_pressao_alto_nivel||';IE_PRESSAO_ALTO_NIVEL_NEW='||NEW.ie_pressao_alto_nivel),
						'A',
						NEW.nr_sequencia,
						null,
						NEW.nm_usuario);

		end if;

		if (OLD.ie_temperatura_alto_nivel <> NEW.ie_temperatura_alto_nivel) then

			CALL cm_gerar_hist_ciclo(	wheb_mensagem_pck.get_texto(311201,'IE_TEMPERATURA_ALTO_NIVEL='||OLD.ie_temperatura_alto_nivel||';IE_TEMPERATURA_ALTO_NIVEL_NEW='||NEW.ie_temperatura_alto_nivel),
						'A',
						NEW.nr_sequencia,
						null,
						NEW.nm_usuario);

		end if;

	end if;

	--desfazendo um ciclo, removemos o histórico/contagem
	if (OLD.dt_fim is not null) and (NEW.dt_fim is null) then

		delete from cm_contagem_ciclo
		where nr_seq_ciclo = NEW.nr_sequencia;

	end if;

end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_cm_ciclo_after() FROM PUBLIC;

CREATE TRIGGER cm_ciclo_after
	AFTER INSERT OR UPDATE ON cm_ciclo FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_cm_ciclo_after();

