SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS cm_conjunto_atual ON cm_conjunto CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_cm_conjunto_atual() RETURNS trigger AS $BODY$
DECLARE
qt_reg_w	smallint;
BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;
if (NEW.QT_MIN_INTERVALO_PREP = 0) then
	--'A quantidade informada nos minutos de preparo deve ser maior que zero.#@#@'
	CALL wheb_mensagem_pck.exibir_mensagem_abort(253265);
end if;

NEW.vl_volume := null;
if (coalesce(NEW.nr_largura,0) > 0) and (coalesce(NEW.nr_altura,0) > 0) and (coalesce(NEW.nr_comprimento,0) > 0) then
	NEW.vl_volume := (NEW.nr_largura * NEW.nr_altura * NEW.nr_comprimento);
end if;

if (TG_OP = 'UPDATE') then

	if (OLD.nm_conjunto <> NEW.nm_conjunto) then
		CALL cm_gerar_hist_conjunto(
				wheb_mensagem_pck.get_texto(311227,'NM_CONJUNTO='||OLD.nm_conjunto||';NM_CONJUNTO_NEW='||NEW.nm_conjunto),
				wheb_mensagem_pck.get_texto(311231),
				NEW.nr_sequencia,
				NEW.nm_usuario);
	end if;

	if (OLD.ie_agendamento <> NEW.ie_agendamento) then
		CALL cm_gerar_hist_conjunto(
				wheb_mensagem_pck.get_texto(311232,'IE_AGENDAMENTO='||OLD.ie_agendamento||';IE_AGENDAMENTO_NEW='||NEW.ie_agendamento),
				wheb_mensagem_pck.get_texto(311240),
				NEW.nr_sequencia,
				NEW.nm_usuario);
	end if;

	if (OLD.ie_administracao <> NEW.ie_administracao) then
		CALL cm_gerar_hist_conjunto(
				wheb_mensagem_pck.get_texto(311244,'IE_ADMINISTRACAO='||OLD.ie_administracao||';IE_ADMINISTRACAO_NEW='||NEW.ie_administracao),
				wheb_mensagem_pck.get_texto(311246),
				NEW.nr_sequencia,
				NEW.nm_usuario);
	end if;

end if;

<<Final>>
qt_reg_w	:= 0;

RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_cm_conjunto_atual() FROM PUBLIC;

CREATE TRIGGER cm_conjunto_atual
	BEFORE INSERT OR UPDATE ON cm_conjunto FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_cm_conjunto_atual();

