DROP TRIGGER IF EXISTS cm_conjunto_cont_insert ON cm_conjunto_cont CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_cm_conjunto_cont_insert() RETURNS trigger AS $BODY$
declare
nr_seq_conj_movto_w		bigint;
qt_usuario_preparo_w	        cm_ciclo.nm_usuario_preparo%type;

BEGIN

select	nextval('cm_conjunto_movto_seq')
into STRICT	nr_seq_conj_movto_w
;

if (coalesce(nr_seq_conj_movto_w,0) > 0) then
	BEGIN

	insert into cm_conjunto_movto(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_conjunto,
		cd_estab_origem,
		cd_estab_atual) values (
			nr_seq_conj_movto_w,
			LOCALTIMESTAMP,
			NEW.nm_usuario,
			LOCALTIMESTAMP,
			NEW.nm_usuario,
			NEW.nr_sequencia,
			NEW.cd_estabelecimento,
			NEW.cd_estabelecimento);

	end;	
end if;


select 	max(nm_usuario_preparo)
into STRICT	qt_usuario_preparo_w
from 	cm_ciclo
where 	nr_sequencia = NEW.nr_seq_ciclo;

if (coalesce(qt_usuario_preparo_w,'X') = 'X') then

	update 	cm_ciclo
	set 	nm_usuario_preparo = NEW.nm_usuario
	where 	nr_sequencia = NEW.nr_seq_ciclo;
	
end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
