SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS cm_conjunto_cont_pend_atual ON cm_conjunto_cont CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_cm_conjunto_cont_pend_atual() RETURNS trigger AS $BODY$
declare
 
qt_reg_w			smallint;
ie_tipo_w			varchar(10);
ie_libera_cme_w			varchar(10);
 
BEGIN 
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N') then 
	goto Final;
end if;
 
select	max(ie_libera_cme) 
into STRICT	ie_libera_cme_w 
from	parametro_medico 
where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;
 
if (coalesce(ie_libera_cme_w,'N') = 'S') then 
	if (NEW.dt_liberacao is null) then 
		ie_tipo_w := 'CJ';
	elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then 
 
		 
		ie_tipo_w := 'XCJ';
	end if;
			 
	if (ie_tipo_w	is not null) then 
		CALL Gerar_registro_pendente_PEP(ie_tipo_w, NEW.nr_sequencia, obter_pessoa_atendimento(NEW.nr_atendimento,'C'), NEW.nr_atendimento, NEW.nm_usuario);
	end if;
end if;
 
<<Final>> 
qt_reg_w	:= 0;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_cm_conjunto_cont_pend_atual() FROM PUBLIC;

CREATE TRIGGER cm_conjunto_cont_pend_atual
	AFTER INSERT OR UPDATE ON cm_conjunto_cont FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_cm_conjunto_cont_pend_atual();

