DROP TRIGGER IF EXISTS cobranca_escritural_delete ON cobranca_escritural CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_cobranca_escritural_delete() RETURNS trigger AS $BODY$
declare

qt_registros_w    bigint;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
select	count(*)
into STRICT	qt_registros_w
from	pls_mensalidade
where	nr_seq_cobranca  = OLD.nr_sequencia;

if (qt_registros_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1025344); --Cobranca vinculada a alguma mensalidade. Nao pode ser excluida!
end if;

end if;

RETURN OLD;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_cobranca_escritural_delete() FROM PUBLIC;

CREATE TRIGGER cobranca_escritural_delete
	BEFORE DELETE ON cobranca_escritural FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_cobranca_escritural_delete();

