SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS compl_pessoa_fisica_atual_hl7 ON compl_pessoa_fisica CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_compl_pessoa_fisica_atual_hl7() RETURNS trigger AS $BODY$
declare
ds_sep_bv_w		varchar(100);
ds_param_integ_hl7_w	varchar(4000);
ie_existe_atend_w 	bigint;
ds_param_integration_w	varchar(500);
BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger = 'S' ) then
	ds_sep_bv_w := obter_separador_bv;

	ds_param_integ_hl7_w :=	'cd_pessoa_fisica=' || NEW.cd_pessoa_fisica || ds_sep_bv_w ||
				'nr_atendimento=0' || ds_sep_bv_w ||
				'nr_seq_interno=0' || ds_sep_bv_w;
	CALL gravar_agend_integracao(18, ds_param_integ_hl7_w);
	
	
	select 	count(*)
	into STRICT 	ie_existe_atend_w
	from	atendimento_paciente
	where 	cd_pessoa_fisica = NEW.cd_pessoa_fisica;
	
	if (ie_existe_atend_w > 0) then
		CALL gravar_agend_integracao(426, ds_param_integ_hl7_w);
	end if;
	
	if (wheb_usuario_pck.is_evento_ativo(853) = 'S') then
		CALL enviar_pf_medico_hl7(NEW.cd_pessoa_fisica,'MUP');		
	end if;
	
	CALL call_bifrost_content('patient.address.update','person_json_pck.get_patient_message_clob('||NEW.cd_pessoa_fisica||')', NEW.nm_usuario);
	
end if;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_compl_pessoa_fisica_atual_hl7() FROM PUBLIC;

CREATE TRIGGER compl_pessoa_fisica_atual_hl7
	AFTER INSERT OR UPDATE ON compl_pessoa_fisica FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_compl_pessoa_fisica_atual_hl7();

